export function ageValidator(minimumAge) {
  const today = new Date();
  const minimumDate = new Date(today.getFullYear() - minimumAge, today.getMonth(), today.getDay());
  return control => {
    const arrayBirthDate = control.value.split('-');
    const birthDate = new Date(arrayBirthDate[0], arrayBirthDate[1], arrayBirthDate[2]);

    if (birthDate > minimumDate) {
      return {
        minimumAge: false
      };
    }

    return null;
  };
}
