import * as i0 from "@angular/core";
import * as i1 from "src/services/auth.service";
import * as i2 from "@angular/router";
import * as i3 from "src/services/displaying-elements.service";
export class AuthGuard {
    constructor(authService, router, displayingElementsService) {
        this.authService = authService;
        this.router = router;
        this.displayingElementsService = displayingElementsService;
    }
    async canActivate(route, state) {
        await this.authService.getAuth();
        console.log('dans le canactivate, token:', this.authService.isLogged);
        if (this.authService.isLogged) {
            this.displayingElementsService.setDisplayNav(true);
            this.displayingElementsService.setDisplayCreation(false);
            return true;
        }
        else {
            console.log('go to /auth/login');
            this.displayingElementsService.setDisplayNav(false);
            this.router.navigateByUrl('/auth/login');
            return false;
        }
    }
}
