import * as moment from 'moment';
import { lastValueFrom } from 'rxjs';
import * as i0 from "@angular/core";
import * as i1 from "@angular/common/http";
import * as i2 from "./server.service";
import * as i3 from "@angular/router";
import * as i4 from "./displaying-elements.service";
moment().format();
export class AuthService {
    constructor(http, 
    // private buddyService: BuddyService,
    serverService, router, displayingElementsService) {
        this.http = http;
        this.serverService = serverService;
        this.router = router;
        this.displayingElementsService = displayingElementsService;
        this.minimumAge = 18;
        this.pictureExtension = [
            'jpg',
            'jpeg',
            'png',
            'bmp',
            'svg',
            'gif',
            'webp',
        ];
        this.passwordPattern = '^[^<>](?=.{8,}$)(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.*\\W).*[^<>]$';
    }
    // newUser!: Buddy;
    ngOnInit() {
        console.log('redémarrage AuthService');
    }
    logout() {
        this.displayingElementsService.setDisplayNav(false);
        // this.displayingElementsService.setDisplayModif(false);
        localStorage.clear();
        this.router.navigateByUrl('/auth/login');
    }
    // public isLoggedIn() {
    //   return moment().isBefore(this.getExpiration());
    // }
    // isLoggedOut() {
    //   return !this.isLoggedIn();
    // }
    getMyToken() {
        // console.log('fetch du token');
        this.myToken = localStorage.getItem('token');
        if (this.myToken != null && this.myToken != undefined) {
            return this.myToken;
        }
        else {
            alert("Problème d'identification, vous allez être déconnecté");
            this.logout();
        }
    }
    getMyId() {
        // console.log('fetch du uuid');
        this.myID = localStorage.getItem('uuid');
        // console.log(this.myID);
        if (this.myID != null && this.myID != undefined) {
            // console.log(this.myID);
            return this.myID;
        }
        else {
            alert("Problème d'identification, vous allez être déconnecté");
            this.logout();
        }
    }
    async getAuth() {
        this.isLogged = await lastValueFrom(this.http.get(this.serverService.serverUrl + '/auth'));
    }
    // getExpiration() {
    //   const expiration = localStorage.getItem('expires_at');
    //   console.log('expiration', expiration);
    //   console.log('moment : ', moment());
    //   // const expiresAt = JSON.parse(expiration);
    //   // return moment(expiresAt);
    //   return 12;
    // }
    authUser(form, route) {
        if (!form.valid) {
            alert('Formulaire non valide!');
        }
        else {
            try {
                this.http
                    .post(this.serverService.serverUrl + route, form.value)
                    .subscribe((authentication) => {
                    if (authentication.success === true) {
                        alert(authentication.message);
                        localStorage.setItem('token', authentication.token);
                        localStorage.setItem('uuid', authentication.user.uuid);
                        // this.buddyService.userIdBuilder(authentication.user);
                        this.router.navigateByUrl('/home');
                    }
                    else {
                        alert(authentication.message);
                    }
                });
            }
            catch (error) {
                console.log(error);
            }
        }
    }
    updatehUser(form, route) {
        if (!form.valid) {
            alert('Formulaire non valide!');
        }
        else {
            try {
                this.http
                    .post(this.serverService.serverUrl + route, form.value)
                    .subscribe((res) => {
                    console.log(res);
                    alert(res);
                    // if (authentication.success === true) {
                    //   alert(authentication.message);
                    //   localStorage.setItem('token', authentication.token);
                    //   localStorage.setItem('uuid', authentication.user.uuid);
                    //   // this.buddyService.userIdBuilder(authentication.user);
                    //   this.router.navigateByUrl('/home');
                    // } else {
                    //   alert(authentication.message);
                    // }
                });
            }
            catch (error) {
                console.log(error);
            }
        }
    }
    requestResetPassword() {
        this.http.get(this.serverService.serverUrl + '/resetpassword');
    }
}
