import * as i1 from "@angular/router";
import * as i2 from "src/services/auth.service";
import * as i3 from "src/services/buddy.service";
import * as i4 from "@angular/common/http";
import * as i5 from "src/services/server.service";
import * as i6 from "./header/header.component";
import * as i7 from "./nav/nav.component";
