export class AppComponent {
    constructor(router, authService, buddyService, http, serverService) {
        this.router = router;
        this.authService = authService;
        this.buddyService = buddyService;
        this.http = http;
        this.serverService = serverService;
        this.title = 'buddyzik';
    }
    ngOnInit() {
        // console.log(
        //   "localStorage.getItem('token') : ",
        //   localStorage.getItem('token')
        // );
        if (localStorage.getItem('token') != null) {
            this.showNav = true;
        }
        else {
            this.showNav = false;
        }
        this.getAuth();
        // alert('ouverture!');
        // this.getAuth();
    }
    getAuth() {
        const ici = this;
        async function checkConnection() {
            if (localStorage.getItem('uuid') != null) {
                ici.authService
                    .getAuth()
                    .subscribe((authorization) => {
                    // console.log('type : ', typeof authorization);
                    // // console.log('type : ');
                    console.log('authorization cuicui : ', authorization);
                    // this.isLogged = authorization.isLogged;
                    if (!authorization) {
                        console.log('pas de token valide');
                        // this.router.navigateByUrl('/login');
                        ici.isLogged = false;
                        // return false;
                    }
                    else {
                        console.log('token valide, on poursuit la nav');
                        // return true;
                        ici.isLogged = true;
                        // ici.router.navigateByUrl('/home');
                    }
                })
                    .unsubscribe();
            }
            else {
                ici.isLogged = false;
            }
        }
        checkConnection();
        console.log('this.isLogged : ', this.isLogged);
        return this.isLogged;
    }
}
