import * as moment from 'moment';
import { lastValueFrom } from 'rxjs';
import * as i0 from "@angular/core";
import * as i1 from "@angular/common/http";
import * as i2 from "./buddy.service";
import * as i3 from "./server.service";
import * as i4 from "@angular/router";
import * as i5 from "./display-nav.service";
moment().format();
export class AuthService {
    constructor(http, buddyService, serverService, router, displayNavService) {
        this.http = http;
        this.buddyService = buddyService;
        this.serverService = serverService;
        this.router = router;
        this.displayNavService = displayNavService;
        this.minimumAge = 18;
        this.pictureExtension = ['jpg', 'jpeg', 'png', 'bmp', 'svg', 'gif'];
        this.passwordPattern = '^[^<>](?=.{8,}$)(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.*\\W).*[^<>]$';
    }
    // newUser!: Buddy;
    ngOnInit() {
        console.log('redémarrage AuthService');
    }
    logout() {
        this.displayNavService.setDisplayNav(false);
        localStorage.clear();
        this.router.navigateByUrl('/auth/login');
    }
    isLoggedIn() {
        return moment().isBefore(this.getExpiration());
    }
    isLoggedOut() {
        return !this.isLoggedIn();
    }
    async getAuth() {
        this.isLogged = await lastValueFrom(this.http.get(this.serverService.serverUrl + '/auth'));
    }
    getExpiration() {
        const expiration = localStorage.getItem('expires_at');
        console.log('expiration', expiration);
        console.log('moment : ', moment());
        // const expiresAt = JSON.parse(expiration);
        // return moment(expiresAt);
        return 12;
    }
    // fetchAutoStartAuth() {
    //   console.log('dans la foncton fetchAutoStartAuth');
    //   return this.http
    //     .get<boolean>(this.serverService.serverUrl + '/auth')
    //     .subscribe((retour) => {
    //       console.log("retour du serveur du check token à l'ouverture: ", retour);
    //       return retour;
    //     });
    // }
    authUser(form, route
    // messageValidation: string,
    // messageError: string
    ) {
        // console.log('dans le service + submitForm');
        // console.log('route : ', route);
        // console.log(form);
        if (!form.valid) {
            alert('Formulaire non valide!');
        }
        else {
            try {
                // console.log('dans le try, le form; ', form.value);
                this.http
                    .post(this.serverService.serverUrl + route, form.value)
                    .subscribe((authentication) => {
                    // console.log('Le serveur a dit ', authentication);
                    // console.log('friends_list ', authentication.user.friends_list);
                    if (authentication.success === true) {
                        alert(authentication.message);
                        localStorage.setItem('token', authentication.token);
                        localStorage.setItem('uuid', authentication.user.uuid);
                        this.buddyService.userIdBuilder(authentication.user);
                        // this.isLogged = true;
                        // console.log('isLogged dans le service = ', this.isLogged);
                        this.router.navigateByUrl('/home');
                    }
                    else {
                        alert(authentication.message);
                    }
                });
            }
            catch (error) {
                console.log(error);
            }
        }
    }
}
