moment().format();
export class AuthService {
    constructor(http, buddyService, serverService, router, displayNavService) {
        this.http = http;
        this.buddyService = buddyService;
        this.serverService = serverService;
        this.router = router;
        this.displayNavService = displayNavService;
        this.minimumAge = 18;
        this.pictureExtension = ['jpg', 'jpeg', 'png', 'bmp', 'svg', 'gif'];
        this.passwordPattern = '^[^<>](?=.{8,}$)(?=.*[a-z])(?=.*[A-Z])(?=.*[0-9])(?=.*\\W).*[^<>]$';
    }
    // newUser!: Buddy;
    ngOnInit() {
        console.log('redémarrage AuthService');
    }
    logout() {
        this.displayNavService.setDisplayNav(false);
        localStorage.clear();
        this.router.navigateByUrl('/auth/login');
    }
    // public isLoggedIn() {
    //   return moment().isBefore(this.getExpiration());
    // }
    // isLoggedOut() {
    //   return !this.isLoggedIn();
    // }
    async getAuth() {
        this.isLogged = await lastValueFrom(this.http.get(this.serverService.serverUrl + '/auth'));
    }
    // getExpiration() {
    //   const expiration = localStorage.getItem('expires_at');
    //   console.log('expiration', expiration);
    //   console.log('moment : ', moment());
    //   // const expiresAt = JSON.parse(expiration);
    //   // return moment(expiresAt);
    //   return 12;
    // }
    authUser(form, route) {
        if (!form.valid) {
            alert('Formulaire non valide!');
        }
        else {
            try {
                this.http
                    .post(this.serverService.serverUrl + route, form.value)
                    .subscribe((authentication) => {
                    if (authentication.success === true) {
                        alert(authentication.message);
                        localStorage.setItem('token', authentication.token);
                        localStorage.setItem('uuid', authentication.user.uuid);
                        this.buddyService.userIdBuilder(authentication.user);
                        this.router.navigateByUrl('/home');
                    }
                    else {
                        alert(authentication.message);
                    }
                });
            }
            catch (error) {
                console.log(error);
            }
        }
    }
}
